import 'package:flutter/material.dart';
import 'package:flutter_application_trabajofinal/app/ui/views/pantalla2/pantalla2.dart';

import 'package:get/get.dart';

class Lista extends StatelessWidget {
  const Lista({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Container(
      width: double.infinity,
      height: 200,
      color: Colors.white,
      margin: const EdgeInsets.only(left: 15.0, right: 15.0),
      child: ListView(
        scrollDirection: Axis.horizontal,
        children: [
          cajas("30-45 min",
              "data:image/jpeg;base64,"),
          //container 1
          cajas("30-45-min",
              "data:image/jpeg;base64,"),
          cajas("20-30 min",
              "https://www.elmundoeats.com/wp-content/uploads/2021/02/FP-Quick-30-minutes-chicken-ramen.jpg"),
        ],
      ),
    );
  }

  Widget cajas(String tiempo, String imagenes) {
    return GestureDetector(
      onTap: () {
        Get.to(const Pantalla2());
      },
      child: Container(
          margin: const EdgeInsets.all(5.0),
          padding: const EdgeInsets.all(25.0),
          width: 460.0,
          height: 100.0,
          decoration: BoxDecoration(
              color: Colors.blue,
              borderRadius: BorderRadius.circular(15.0),
              image: DecorationImage(
                  image: NetworkImage(imagenes), fit: BoxFit.cover)),
          child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Container(
                          width: 80.0,
                          height: 35.0,
                          child: Text(tiempo,
                              style: const TextStyle(
                                color: Colors.white,
                              ))),
                    ]),
              ])),
    );
  }
}
